  return (
    <div className={[classes.root && "dashbord"]}>
      <Grid
        container
        spacing={2}
        direction="column"
        justify="flex-start"
        alignItems="stretch"
      >
        <Result result={this.state.result}/> 
        <NewButton onClick={this.onClick}/>
       </Grid> 
    </div>
  );
};
};
export default Dashbord;
