                activeKey={key}
                onSelect={handleChange(item.id)}
                onActiveTab={() => handleActive(item.id)}
              >
                <TabList>
                  <Tab className={item.class}>{item.name}</Tab>
                </TabList>
              </Tabs>
              <CustomTabPanel value={value} index={item.id} children={item.name} />
            </div>
          ))}
        </div>
      );
    }
  }
};
export default ToolBar;
