import React, { useState } from "react";
import "./toolbar.css";
import ToolbarData from "./toolbar.json";
import { makeStyles } from "@material-ui/core/styles";
import {Tabs,Tab,TabList} from "react-tabs";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    backgroundColor: theme.palette.background.paper,
  },
}));

const ToolBar = (props) => {
  const classes = useStyles();
  const [value, setValue, key, setKey] = useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };
  const url = window.location.href.split("/").pop();
  const vals = Object.keys(ToolbarData);
  for (var i = 0; i < vals.length; i++) {
    if (vals[i] === url) {
      const list = vals[i];
      return (
        <div className="toolbar">
          {Object.values(ToolbarData[list]).map((item) => (
            <div className={classes.root}>
              <Tabs
                value={value}
                activeKey={key}
                onSelect={(k) => setKey(k)}
                onChange={handleChange}
              >
                <TabList>
                  <Tab className={item.class}>{item.name}</Tab>
                </TabList>
              </Tabs>
              <TabPanel value={value} index={item.id}>
                {item.name}
              </TabPanel>
            </div>
          ))}
        </div>
      );
    }
  }
};
export default ToolBar;
