import React, { useState } from "react";
import "./toolbar.css";
import ToolbarData from "./toolbar.json";
import { compose, withState, withHandlers } from 'recompose'
import styled from 'styled-components'

const enchancer = compose(
	withState('selectedTabId', 'setSelectedTabId', 1),
	withHandlers({
		isActive: props => id => {
			return props.selectedTabId === id
		},
		setActiveTab: props => id => {
			props.setSelectedTabId(id)
		},
	})
)
const Tabs = enchancer(props => {
	return (
		<ul>
			{props.data.map((el, i) => {
				return (
					<Tab
						key={i}
						content={el.name}
						isActive={props.isActive(el.id)}
						onActiveTab={() => props.setActiveTab(el.id)}
					/>
				)
			})}
		</ul>
	)
})
const Tab = props => {
	return (
		<StyledLi isActive={props.isActive} onClick={props.onActiveTab}>
			<p>{props.content}</p>
		</StyledLi>
	)
}

const StyledLi = styled.li`
  font-weight: ${({ isActive }) => (isActive ? 600 : 100)};
  cursor: pointer;
  font-family: Helvetica;
  transition: 200ms all linear;
`
const ResponsiveNavbarView = (props) => {
//   const [value, setValue] = useState(0);
//   const isActive = (props) => (id) => {
//     return value === id;
//   };
//   const handleActive = props => id => {
//     setValue(id);
//   };
//   const classActive = (id ,key)=>{
//     if (id === 0 || id === key){
//         return "active";
//     }
//     else{
//         return "";
//     }
//   } 
  const url = window.location.href.split("/").pop();
  const vals = Object.keys(ToolbarData);
  for (var i = 0; i < vals.length; i++) {
    if (vals[i] === url) {
      const list = vals[i];
      return (
        <div className="toolbar">
          {Object.values(ToolbarData[list]).map((item, i) => (
            <div className={classActive(item.id,i)}>
              <ul key={i} className="toolbarItems" active={isActive(item.id)}>
                <li>
                  {item.name}
                </li>
              </ul>
            </div>
          ))}
        </div>
      );
    }
  }
};
export default  ResponsiveNavbarView;
